import{_ as p,c as t,o as e,d as _}from"./app.de1979c9.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"\u5176\u4ED6/\u5DE5\u4F5C/\u5176\u4ED6/\u7535\u5546.md"}'),o={name:"\u5176\u4ED6/\u5DE5\u4F5C/\u5176\u4ED6/\u7535\u5546.md"},a=_("<p>\u95EE\u9898\u8D8A\u591A\u8D8A\u597D\uFF0C \u6700\u6015\u7684\u662F\u6CA1\u6709\u95EE\u9898\u3002 &quot;\u95EE\u9898&quot;\u662F\u4EC0\u4E48\uFF1F \u95EE\u9898\u5C31\u662F\u9700\u6C42\u7684\u4EA7\u51FA\u7684\u524D\u63D0\uFF0C\u6709\u9700\u6C42\uFF0C\u4EA7\u54C1\u624D\u80FD\u8FDB\u4E00\u6B65\u5B8C\u5584\uFF0C\u7528\u6237\u624D\u4F1A\u559C\u6B22\uFF0C\u6216\u8005\u8BF4\u8BA9\u4EA7\u54C1\u9500\u91CF\u66F4\u597D</p><p>\u76EE\u7684\uFF08\u521D\u8877\uFF09\uFF1A\u4E3A\u4EC0\u4E48\u60F3\u505A\u9879\u76EE\uFF0C\u60F3\u81EA\u5DF1\u5355\u5E72\u3001\u521B\u4E1A\uFF0C\u73B0\u5728\u662F\u4E00\u4E2A\u4FE1\u606F\u7684\u65F6\u4EE3\uFF0C\u5012\u5356\u4FE1\u606F\u5DEE\uFF0C\u8BA9\u4F60\u589E\u52A0\u76C8\u5229\u70B9\uFF0C\u5E76\u4E14\u81EA\u5DF1\u5904\u4E8E\u4E92\u8054\u7F51\u4E4B\u4E2D\uFF0C\u514D\u53BB\u9694\u884C\u4E4B\u75DB\u3002 \u4E14\u73B0\u5728\u6709\u53CC\u8D44\u6E90\uFF0C\u4F4E\u52B3\u52A8\u6210\u672C\u83B7\u53D6\u957F\u6536\u76CA\u3002</p><p>\u524D\u8A00\uFF1A\u4E3A\u4EC0\u4E48\u8981\u505A\u7EBF\u4E0A\uFF1F 1.\u8DB3\u4E0D\u51FA\u6237\u5C31\u53EF\u4EE5\u5B8C\u6210\u5F88\u591A\u4E8B\u60C5\uFF0C\u4E0D\u9700\u8981\u5C40\u9650\u5728\u67D0\u4E2A\u5730\u533A\uFF0C\u53EF\u4EE5\u9762\u5411\u5168\u56FD 2.\u4EA7\u54C1\u4E0D\u53D7\u5C40\u9650\uFF0C\u53EF\u4EE5\u505A\u5927\u505A\u5F3A 3.\u7701\u65F6\u9AD8\u6548\uFF0C\u5F53\u4ECA\u793E\u4F1A\u7684\u751F\u6D3B\u4E60\u60EF\uFF0C\u8DDF\u968F\u65F6\u4EE3\u524D\u8FDB 4.\u5C31\u8FD1\u539F\u5219\uFF0C\u62C5\u5FC3\u4E92\u8054\u7F51\u7684\u6C34\u6DF1\uFF1F\u6BD5\u7ADF\u8FD9\u662F\u4E00\u4E2A\u4E32\u884C\u4E1A\u7684\uFF0C\u6240\u4EE5\u5C31\u8FD1\u53D6\u4F18\u8D44\u6E90\uFF0C\u8FBE\u52300\u6210\u672C\uFF0C0\u98CE\u9669\u3002 5.\u6210\u719F\u7684\u7EBF\u4E0A\u6559\u80B2\uFF0C\u4E92\u8054\u7F51\u591A\u6001 6.\u7EBF\u4E0A\u4E5F\u5C5E\u4E8E\u521D\u671F\uFF0C\u76EE\u524D\u7EBF\u4E0B\u4E5F\u9700\u8981\u505A\uFF0C\u4F46\u662F\u9700\u8981\u6162\u6162\u8F6C\u578B\u5230\u7EBF\u4E0A\uFF0C\u628A\u73B0\u6709\u5BA2\u6237\u6536\u96C6\u5230\u7EBF\u4E0A</p><p>\u4E86\u89E3\u4EA7\u54C1\u3001\u6316\u6398\u63D0\u70BC\u4EA7\u54C1\u7684\u6838\u5FC3\u5356\u70B9</p><p>\u4E00\u3001\u7528\u6237\u7FA4\u4F53\uFF0C\u5BA2\u6237\u4EF7\u503C 1.\u6301\u7EED\u6027\u5BA2\u6237 2.\u53EF\u53D1\u5C55\u5BA2\u6237 3.\u53EF\u63A8\u5E7F\u7528\u6237 4.\u53EF\u5408\u4F5C\u6E20\u9053\uFF0C\u9002\u5408\u8584\u5229\u591A\u9500\u3002\u4F8B\uFF1A\u516C\u53F8\u3001\u5B66\u6821\u7B49\u7EC4\u7EC7\u7FA4\u4F53</p><p>\u4E8C\u3001\u4EA7\u54C1\u5356\u70B9\u3001\u4EA7\u54C1\u53D7\u4F17\u70B9 \u4EF7\u683C\u9002\u4E2D\uFF0C\u4EA7\u54C1\u591A\u6837\uFF0C\u53EF\u63A5\u53D7\u591A\u7FA4\u4F53\u3001\u591A\u4EF7\u683C\u7B49\u53D7\u4F17\u89C2\u70B9\u3002 \u5927\u54C1\u724C\uFF0C\u6709\u4FDD\u969C</p><p>\u4E09\u3001\u6392\u5217\u53D7\u4F17\u4EA7\u54C1\uFF0C\u89E3\u6790\u4EA7\u54C1\u4F18\u7F3A\u70B9 \u7528\u6237\u4F1A\u5173\u6CE8\u5B9E\u7269\u548C\u4EF7\u683C\uFF0C\u7B2C\u4E00\u5438\u5F15\u70B9\u5728\u4E8E\u56FE\u7247\uFF0C\u7136\u540E\u5728\u786E\u5B9A\u4EF7\u683C\u662F\u5426\u6EE1\u610F\u3002 \u7528\u6237\u4EBA\u7FA4\u4E5F\u5927\u4E0D\u540C\uFF0C\u5B66\u751F\u3001\u793E\u4F1A\u7FA4\u4F53\u3001\u516C\u53F8\u7FA4\u4F53\u6240\u5728\u610F\u7684\u5C42\u9762\u4E0D\u540C\uFF0C\u6240\u4EE5\u4EA7\u54C1\u6295\u653E\u5E73\u53F0\u4E5F\u8981\u66F4\u5206\u5E03\u660E\u786E\uFF0C\u867D\u7136\u53EF\u4EE5\u5E7F\u6CDB\u5E03\u5C40\uFF0C\u4F46\u662F\u5FC5\u987B\u6709\u7CBE\u54C1\uFF0C\u53EA\u60F3\u7740\u649E\u5927\u8FD0\u662F\u5B8C\u5168\u4E0D\u884C\u7684\uFF0C\u9700\u8981\u771F\u6750\u5B9E\u6599</p><p>\u56DB\u3001\u5BF9\u6BD4\u5DF2\u6709\u8D44\u6E90\u4E0E\u7ADE\u54C1\u8D44\u6E90\uFF0C\u63D0\u70BC\u5BF9\u7ADE\u54C1\u7684\u4F18\u52BF \u76EE\u524D\u6240\u83B7\u5F97\u7684\u4FE1\u606F\uFF0C\u7ADE\u54C1\u5546\u5BB6\u8D27\u6E90\u548C\u4EF7\u683C\u57FA\u672C\u5F62\u540C\uFF0C\u4E3B\u8981\u7A81\u51FA\u4E8E\u56FE\u7247\u7684\u7F8E\u611F</p><p>\u4E94\u3001\u7EBF\u4E0A\u9500\u552E\u6E20\u9053\u6709\u54EA\u4E9B\uFF1F \u76EE\u524D\u9488\u5BF9\u4EBA\u7FA4\u4E3A\uFF1A\u5B66\u751F\u3001\u793E\u4F1A\u7FA4\u4F53\u3001\u516C\u53F8 \u793E\u4F1A\u7FA4\u4F53\uFF1A \u54B8\u9C7C\u3001\u62FC\u591A\u591A\u300158\u3001\u8D76\u96C6\u7F51 \u516C\u53F8\uFF1A\u9700\u8981\u901A\u8FC7\u4FE1\u4EFB\u6162\u6162\u57F9\u517B \u5B66\u751F\uFF1A\u4EE5\u7EBF\u4E0B\u63A8\u5E7F\u7684\u5F62\u5F0F\u5C55\u5F00\uFF0C\u4FE1\u4EFB\u548C\u53E3\u7891\u5F85\u4EBA\u3002 \u533B\u9662\uFF1A \u9488\u5BF9\u7528\u6237\u7FA4\u4F53\uFF0C\u6240\u63A8\u5E7F\u7684\u6548\u7387\u5B8C\u5168\u4E0D\u540C\uFF0C\u9700\u7075\u6D3B\u5E94\u53D8\u3002 \u9700\u8981\u5148\u79EF\u6512\u7528\u6237\uFF0C\u6709\u7528\u6237\u7FA4\u4F53\u540E\u624D\u4F1A\u6709\u4EBA\u4E70\u5355</p><p>\u516D\u3001\u7528\u6237\u7FA4\u4F53\u7684\u53CD\u9988 \u9664\u4E86\u94B1\u4EE5\u5916\uFF0C\u8FD8\u9700\u8981\u65E9\u524D\u4ECE\u7528\u6237\u8EAB\u4E0A\u83B7\u53D6\u53CD\u9988\uFF0C\u8FD9\u5C31\u662F\u6240\u8C13\u7684\u201C\u95EE\u9898\u201D\uFF0C\u95EE\u9898\u5373\u662F\u9700\u6C42\u3002 \u4F60\u4E3A\u4EC0\u4E48\u4F1A\u4E70\uFF1F\u670B\u53CB\u63A8\u8350\uFF1F\u54C1\u724C\u6548\u5E94\uFF1F\u4EA7\u54C1\u8D44\u8D28\u597D\uFF1F\u670D\u52A1\u548C\u4EA7\u54C1\u8D44\u8D28\u5171\u540C\u6548\u5E94\uFF1F \u4EF7\u683C\u548C\u4EA7\u54C1\u8D44\u8D28\u5171\u540C\u6548\u5E94\uFF1F\u591A\u79CD\u53CD\u9988\u9700\u8981\u6536\u96C6</p><p>\u4E00\u4E2A\u597D\u7684\u5F00\u7AEF\uFF0C\u9700\u8981\u6DF1\u5165\u6C9F\u901A</p><p>\u4F46\u662F\u968F\u7740\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u3001\u76F4\u64AD\u3001\u77ED\u89C6\u9891\u7684\u5174\u8D77\uFF0C\u5FAE\u5546\u5728\u670B\u53CB\u5708\u7684\u5E7F\u544A\u53D8\u5F97\u65E0\u4EBA\u95EE\u6D25\u3002</p><p>\u88AB\u5C4F\u853D\u3001\u88AB\u62C9\u9ED1\u3001\u505A\u5FAE\u5546\u7684\u7528\u6237\u88AB\u65C1\u4EBA\u201C\u5F02\u6837\u201D\u770B\u5F85\uFF01</p><p>\u5FAE\u4FE1\u670B\u53CB\u5708\u5356\u8D27\u5DF2\u7136\u88AB\u65B0\u5174\u7684\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u3001\u76F4\u64AD\u3001\u77ED\u89C6\u9891\u7B49\u5404\u79CD\u5356\u8D27\u6E20\u9053\u6DD8\u6C70\u3002 \xA0 \u975E\u539F\u751F\u7684\u5FAE\u5546\u57CE\u5B58\u5728\u7740\u5404\u79CD\u5F0A\u7AEF\uFF0C\u6BD4\u5982\u8BF4\u4F53\u9A8C\u4E0D\u597D\u3001\u5206\u4EAB\u4E0D\u8D77\u773C\u3001\u65E0\u6CD5\u79EF\u7D2F\u539F\u59CB\u7528\u6237\u7B49\u7B49\u3002\u8FD9\u5BF9\u5FAE\u5546\u6765\u8BF4\u90FD\u662F\u975E\u5E38\u4E0D\u5229\u7684\u3002</p><p>\u5176\u6B21\u5FAE\u5546\u57CE\u7684\u5F00\u53D1\u95E8\u69DB\u9AD8\uFF0C\u9700\u8981\u6536\u85CF/\u6216\u4E13\u95E8\u5173\u6CE8\u516C\u4F17\u53F7/\u901F\u5EA6\u6162\u7B49\u7B49\u5F0A\u7AEF</p><p>\u4E00\u4E2A\u5FAE\u4FE1\u53F75000\u4E2A\u4EBA\u76F8\u6BD4\u62DF\u7684\uFF0C\u4F55\u51B5\u8FD8\u6CA1\u6709\u7B97\u4E0A\u5C4F\u853D\u4F60\u7684\u3001\u4EE5\u53CA\u4EB2\u621A\u670B\u53CB\u7B49\u4E2A\u522B\u7528\u6237\u3002</p><p>\u793E\u7FA4\u88C2\u53D8\uFF0C\u7528\u6237\u5206\u53D1</p><p>\u4F9D\u65E7\u662F\u4EE5\u5FAE\u5546\u53D1\u52A8\u6001\u4E3A\u4F8B\uFF0C\u4E00\u5929\u53D15-10\u4E2A\uFF0C\u4E5F\u5C31\u6700\u591A\u5341\u51E0\u4E2A\u4E86\uFF01\u53D1\u518D\u591A\u5C06\u4F1A\u88AB\u5BA2\u6237\u5C4F\u853D\uFF0C\u5F97\u4E0D\u507F\u5931\uFF01</p><p>\u800C\u5FAE\u4FE1\u5C0F\u7A0B\u5E8F\u5462\uFF01\u4F60\u53EF\u4EE5\u5728\u4E0A\u9762\u505A\u65B0\u54C1\u63A8\u9001\u3001\u7B7E\u5230\u6709\u793C\u3001\u6D3B\u52A8\u8F6C\u53D1\u51CF\u514D\u7B49\u5404\u79CD\u6D3B\u52A8\u4FC3\u9500\uFF0C\u4ECE\u800C\u518D\u5229\u7528\u4E2A\u4EBA\u5206\u4EAB\u6E20\u9053\u5206\u53D1\u5F62\u6210\u88C2\u53D8\u3002</p><p>\u5FAE\u5546\u4E00\u5929\u53D1\u5E035\u52A8\u6001\uFF0C\u4EE5\u770B\u5230\u7684\u7528\u6237\u4E3A\u4F8B\u6700\u591A2000\u591A\u4E2A\u8DB3\u591F\u591A\u4E86\u3002\u800C\u5FAE\u5546\u5728\u5C0F\u7A0B\u5E8F\u5546\u57CE\u4E0A\u9762\u53D1\u5E03\u4E00\u4E2A\u65B0\u54C1\uFF0C\u8BA9\u7528\u6237\u53C2\u4E0E\u62FC\u56E2\u8F6C\u53D1\u90A3\u5C31\u662F2000\u4E2A\u4E58\u4EE5N+\u65E0\u6570\u4E2A\u4E86\u3002</p><p>\u53EF\u4EE5\u8F6C\u53D1\u5230\u670B\u53CB\u3001\u4EB2\u621A\u3001\u53EF\u4EE5\u8F6C\u53D1\u5230\u7FA4\u3001\u4E5F\u53EF\u4EE5\u8F6C\u53D1\u5230\u5176\u4ED6\u6E20\u9053\uFF01\u8FD9\u6837\u5BF9\u6BD4\u4F60\u8FD8\u89C9\u5F97\u5C0F\u7A0B\u5E8F\u6CA1\u6709\u4F18\u52BF\u5417\uFF1F</p><p>\u5F62\u5F0F\u53EF\u4EB2\u6C11\u53EF\u5B98\u65B9\u6B63\u89C4</p><p>\u8DDF\u7740\u65F6\u4EE3\u7684\u811A\u6B65 \u9760\u5237\u5C4F\u670B\u53CB\u5708\u6765\u8D70\u8D27\u7684\u521D\u7EA7\u6A21\u5F0F\u5DF2\u6210\u8FC7\u53BB\uFF0C\u5FAE\u5546\u9700\u8981\u4E00\u4E2A\u6548\u7387\u66F4\u5FEB\u3001\u66F4\u7A33\u5B9A\u7684\u6E20\u9053\u6765\u51FA\u8D27\u3002</p>",23),s=[a];function c(r,n,d,i,l,m){return e(),t("div",null,s)}const T=p(o,[["render",c]]);export{u as __pageData,T as default};
